        if (element.length>0) {
            $(element).css('text-align', how);
            localStorage.setItem('format_'+selector+'_text-align', how);
            ;;;console.log('LocalStorage for "'+selector+'" was set to "'+localStorage.getItem('format_'+selector+'_text-align')+'"');
        }
